import React from "react";

function InputArea(props) {
    <div className="form">
        <input onChange = {props.handleChange} type="text"  value = {propsinputText}/>
        <button onClick={props.addItem}>
          <span>Add</span>
        </button>
    </div>
}

export default InputArea;
